            }).default(0).run(conn, callback);

r.table('words').orderBy('id').fold('', function (acc, word) {
                return acc.add(r.branch(acc.eq(''), '', ', ')).add(word);
            }).run(conn, callback);

r.table('words').orderBy('id').fold('', function (acc, word) {
                return acc.add(r.branch(acc.eq(''), '', ', ')).add(word);
            }).run(conn, callback);

